import React from 'react';
import {HomeContent} from './Components/home-content.jsx';
import {PhotoshopContent} from './Components/photoshop-content.jsx';
import {GamedevContent} from './Components/gamedev-content.jsx';
import {ModelingContent} from './Components/modeling-content.jsx';
import {ContactContent} from './Components/contact-content.jsx';
import {LoginContent} from './Components/database/log-in/login-content.jsx';
import {AdminContent} from './Components/database/admin-content.jsx';
import {Navbar} from './Components/navbar.jsx';
import { Footer } from './Components/footer.jsx';
import './App.css';
import { BrowserRouter, Route } from "react-router-dom";

export class Authentication extends React.Component {

  constructor() {
    super();
    this.state = {
      loggedIn: false,
      loggedInText: "LOGIN"
    };
    this.logIn = this.logIn.bind(this);
  }

  logIn() {
    this.setState({
      loggedIn: true,
      loggedInText: "LOGGED IN!"
    })
  }

  render () {
    return (<div>ok</div>);
  }
}

const authentication = new Authentication();

function Login() {
  return (
    <div className="App">
      <Navbar auth={authentication.state.loggedIn} logtext={authentication.state.loggedInText}/>
      <LoginContent handler = {authentication.logIn}/>
      <Footer/>
    </div>
  );
}

function Admin() {
  return (
    <div className="App">
      <Navbar auth={authentication.state.loggedIn}/>
      <AdminContent auth={authentication.state.loggedIn}/>
      <Footer/>
    </div>
  );
}

function Home() {
  return (
    <div className="App">
      <Navbar auth={authentication.state.loggedIn}/>
      <HomeContent authenticated={authentication.state.loggedIn}/>
      <Footer/>
    </div>
  );
}

function Photoshop() {
  return (
    <div className="App">
      <Navbar auth={authentication.state.loggedIn}/>
      <PhotoshopContent auth={authentication.state.loggedIn}/>
      <Footer/>
    </div>
  );
}

function Modeling() {
  return (
    <div className="App">
      <Navbar auth={authentication.state.loggedIn}/>
      <ModelingContent auth={authentication.state.loggedIn}/>
      <Footer/>
    </div>
  );
}

function Gamedev() {
  return (
    <div className="App">
      <Navbar auth={authentication.state.loggedIn}/>
      <GamedevContent auth={authentication.state.loggedIn}/>
      <Footer/>
    </div>
  );
}

function Contact() {
  return (
    <div className="App">
      <Navbar auth={authentication.state.loggedIn}/>
      <ContactContent auth={authentication.state.loggedIn}/>
      <Footer/>
    </div>
  );
}


function AppRouter() {
  return (
    <BrowserRouter>
      <div>
        <Route path="/" exact component={Home} />
        <Route path="/photoshop/" component={Photoshop} />
        <Route path="/modeling/" component={Modeling} />
        <Route path="/gamedev/" component={Gamedev} />
        <Route path="/contact/" component={Contact} />
        <Route path="/login/" component={Login} />
        <Route path="/admin/" component={Admin} />
      </div>
    </BrowserRouter>
  );
}

export default AppRouter;
