import React from 'react'

var userArray = [];

export class RegisterContent extends React.Component {
    
    constructor() {
        super();
        this.handleSubmit = this.handleSubmit.bind(this);
    }

      // Handles the submission of the add book form and adds it to the database
      handleSubmit(event) {
        event.preventDefault();
        fetch('http://localhost:4200/api/users', {
            method: 'post',
            headers: {'Content-Type':'application/json'},
            body: JSON.stringify({
             "FirstName": this.FirstName.value,
             "LastName": this.LastName.value,
             "Email": this.Email.value,
             "Password": this.Password.value,
             "Admin": this.Admin.value,
            })});
        console.log("User registered");
    }
     
    render() {

        /*function dataHandle() {
            this.props.callbackFromParent(userArray);
        }*/

        return(
            <div className="registerContainer">
                <header>
                    <h1>Register</h1>
                </header>
                <div className='registerForm'>
                    <form onSubmit={this.handleSubmit}>
                    <label>
                        First Name:
                        <input ref={(ref) => {this.FirstName = ref}} type="text" id="title" name="firstname" autocomplete="off"/>
                    </label><br></br>
                    <label>
                        Last Name:
                        <input ref={(ref) => {this.LastName = ref}} type="text" id="author" name="lastname" autocomplete="off"/>
                    </label><br></br>
                    <label>
                        Email:
                        <input ref={(ref) => {this.Email = ref}} type="email" id="date" name="email" autocomplete="off"/>
                    </label><br></br>
                    <label>
                        Password:
                        <input ref={(ref) => {this.Password = ref}} type="password" id="file" name="password" autocomplete="off"/>
                    </label><br></br>
                    <input type="submit" id="Submit"/>
                    </form>
                </div>
            </div>
        )
    }
}
