import React from 'react'

export class RegisterContent extends React.Component {
    
    constructor() {
        super();
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleSubmit(event) {
        event.preventDefault();
        fetch('http://localhost:4200/api/users', {
            method: 'post',
            headers: {'Content-Type':'application/json'},
            body: JSON.stringify({
             "FirstName": this.FirstName.value,
             "LastName": this.LastName.value,
             "Email": this.Email.value,
             "Password": this.Password.value,
            })});
        console.log("User registered");
    }
     
    render() {
        return(
            <div className="registerContainer">
                <header>
                    <h1>Register</h1>
                </header>
                <div className='registerForm'>
                    <form onSubmit={this.handleSubmit}>
                    <label>
                        First Name:
                        <input ref={(ref) => {this.FirstName = ref}} type="text" id="title" name="firstname"/>
                    </label><br></br>
                    <label>
                        Last Name:
                        <input ref={(ref) => {this.LastName = ref}} type="text" id="author" name="lastname"/>
                    </label><br></br>
                    <label>
                        Email:
                        <input ref={(ref) => {this.Email = ref}} type="email" id="date" name="email"/>
                    </label><br></br>
                    <label>
                        Password:
                        <input ref={(ref) => {this.Password = ref}} type="password" id="file" name="password"/>
                    </label><br></br>
                    <input type="submit" id="Submit"/>
                    </form>
                </div>
            </div>
        )
    }
}
