import React from 'react'
import './register-content.css';

export class GetContent extends React.Component {
    
    constructor() {
        super();
        this.setID = this.setID.bind(this); 
        this.state = {
            id: 1,
            js: null,
        }  
    } 

    getUser() {
        console.log(this.state.id);
        fetch('http://localhost:4200/api/users/' + this.state.id, {
            method: 'get'
        }).then(res =>
            res.json().then(json => {
                console.log("OK");
                this.setState({ js: json });
                console.log(json[0].ID);
                //return(<div>"ID: " + json[0].ID + "\nFirst Name: " + json[0].FirstName + "\nLast Name: " + json[0].LastName + "\nEmail: " + json[0].Email + "\nPassword: " + json[0].Password</div>);
            })
        );
    }

    setID(event) {
        event.preventDefault();
        this.setState({id:this.ID.value}, this.getUser);
        //window.location.reload();
    }

    componentDidMount () {
        this.getUser();
    }
     
    render() {
        const { js } = this.state;
        return(
            <div className="register-container">
                <header>
                    <h1>Get Account</h1>
                </header>
                <div className='register-form'>
                    <form onSubmit={this.setID}>
                    <label>
                        ID:
                        <input ref={(ref) => {this.ID = ref}} type="number" id="input-firstname" name="id"/>
                    </label><br></br>
                    <input type="submit"/>
                    </form>
                </div>
                <div>{js && "ID: " + this.state.js[0].ID}<br></br>
                        {js && "First Name: " + this.state.js[0].FirstName}<br></br>
                        {js && "\nLast Name: " + this.state.js[0].LastName}<br></br>
                        {js && "\nEmail: " + this.state.js[0].Email}<br></br>
                        {js && "\nPassword: " + this.state.js[0].Password}</div>
                {/* <this.GetUser id={this.state.id}/> */}
            </div>
        )
    }
}
