import React from 'react'
import './register-content.css';

export class GetContent extends React.Component {
    
    constructor() {
        super();
        this.setID = this.setID.bind(this);
    }

    state = {
        id: 1,
        js: [],
    }

    GetUser(event, props) {
        fetch('http://localhost:4200/api/users/' + parseInt(props.id) , {
            method: 'get'
        }).then(res =>
            res.json().then(json => {
                this.setState({ js: json });
                //return(<div>"ID: " + json[0].ID + "\nFirst Name: " + json[0].FirstName + "\nLast Name: " + json[0].LastName + "\nEmail: " + json[0].Email + "\nPassword: " + json[0].Password</div>);
            })
        );
        /*return(
            <div>Egg</div>
            );*/
    }

    setID(event) {
        this.setState(this.ID.value);
        this.window.refresh();
    }

    componentWillMount () {
        this.GetUser(this.state.id);
    }
     
    render() {
        return(
            <div className="register-container">
                <header>
                    <h1>Get Account</h1>
                </header>
                <div className='register-form'>
                    <form onSubmit={this.setID}>
                    <label>
                        ID:
                        <input ref={(ref) => {this.ID = ref}} type="number" id="input-firstname" name="id"/>
                    </label><br></br>
                    <input type="submit"/>
                    </form>
                </div>
                <this.GetUser id={this.state.id}/>
            </div>
        )
    }
}
