import React from 'react'
import './register-content.css';

function GetUser(props) {
    fetch('http://localhost:4200/api/users/' + parseInt(props.id) , {
        method: 'get'
    }).then(res =>
        res.json().then(json => {
            return(
            <div>Egg</div>
            );
            alert("ID: " + json[0].ID + "\nFirst Name: " + json[0].FirstName + "\nLast Name: " + json[0].LastName + "\nEmail: " + json[0].Email + "\nPassword: " + json[0].Password);
        })
    );
}

export class GetContent extends React.Component {
    
    constructor() {
        super();
        this.setID = this.setID.bind(this);
    }

    state = {
        id: 1,
    }

    setID(event, index) {
        this.setState({id:index});
        this.window.refresh();
    }
     
    render() {
        return(
            <div className="register-container">
                <header>
                    <h1>Get Account</h1>
                </header>
                <div className='register-form'>
                    <form onSubmit={this.setID}>
                    <label>
                        ID:
                        <input ref={(ref) => this.setState({id:ref})} type="number" id="input-firstname" name="id"/>
                    </label><br></br>
                    <input type="submit"/>
                    </form>
                </div>
                <GetUser id={this.state.id}/>
            </div>
        )
    }
}
