import React from 'react';
import blank from './Images/blank.png'
import './gamedev-content.css';

export class GamedevContent extends React.Component {

    constructor() {
        super();
        this.state = {
            js: undefined,
            content: undefined
        }
    }

    getPhotos () {
        fetch('http://localhost:4200/api/gamedev/', {
            method: 'get'
        }).then(res =>
            res.json().then(json => {
                this.setState({ js: json});
            })
        );
    }

    getArticles () {
        var jsonFinal = "";
        for(var i = 0; i < this.state.js.length; i++) {
            jsonFinal +=    "<div className='section'>" +
                                "<div className='section-info'>" +
                                    "<h2 className='section-title'>" + this.state.js[i].Title +"</h2>" +
                                    "<p className='section-description'>" + this.state.js[i].Description + "</p>" +
                                "</div>" +
                                "<img className='section-image' src=" + this.state.js[i].ImageUrl + "/>" +
                                "<div className='clear'/>" +
                            "</div>";
        }
        this.setState({content: jsonFinal})
    }

    componentDidMount () {
        this.getPhotos();
    }

    render () {
        const { js } = this.state;
        return (
            <div className="gamedev-content">
                <br></br>
                <br></br>
                <h1>6 Years of Unity3D Game and Assets</h1>
                
                {js && <div className="column" dangerouslySetInnerHTML={{ __html: this.state.content }}></div>}

                <div className="clear"/>
            </div>
        );
    }
};
