import { LOGIN_PENDING, LOGIN_SUCCESS, LOGIN_ERROR, setLoginPending, SetLoginSuccess, SetLoginError } from '../Actions/index';

export function login(username, password) {
    return dispatch => {
        dispatch(setLoginPending(true));
        dispatch(SetLoginSuccess(false));
        dispatch(SetLoginError(false));

        sendLoginRequest(username, password)
            .then(success => {
                dispatch(setLoginPending(false));
                dispatch(SetLoginSuccess(true));
            })
            .catch(error => {
                dispatch(setLoginPending(false));
                dispatch(SetLoginError(error));
            })
    }
}

export default function reducer(state = {
    isLoginPending: false,
    isLoginSuccess: false,
    isLoginError: null
}, action) {
    switch(action.type) {
        case LOGIN_SUCCESS:
            return {
                ...state,
                isLoginSuccess: action.isLoginSuccess
            };

        case LOGIN_PENDING:
            return {
                ...state,
                isLoginPending: action.isLoginPending
            };

        case LOGIN_ERROR:
            return {
                ...state,
                isLoginError: action.isLoginError
            };

        default:
            return state;
    }
}

/* Handles login credential checking for user, checks their email and password within
the user database and returns whether the login was successful or not. */
function sendLoginRequest (email, password) {
    return new Promise((resolve, reject) => {
        fetch('http://localhost:4200/api/users/' + email, {
            method: 'get'
        }).then(res =>
            res.json().then(json => {
                if(json.length > 0) {
                    if(json[0].Password === password) {
                        return resolve(true);
                    }
                }
                return reject(new Error("Invalid username or password"));
            })
        );
    });
}
