import React from 'react'
import {RegisterContent} from './register/register-content.jsx';
import {GetUser} from './register/register-content.jsx';
import {UpdateUser} from './register/register-content.jsx';
import {DeleteUser} from './register/register-content.jsx';

function test ()
{

}

export class AdminContent extends React.Component {  
    
    constructor() {
        super();
    }

    register(event) {
        event.preventDefault();
        fetch('http://localhost:4200/api/users', {
            method: 'post',
            headers: {'Content-Type':'application/json'},
            body: JSON.stringify({
             "FirstName": this.FirstName.value,
             "LastName": this.LastName.value,
             "Email": this.Email.value,
             "Password": this.Password.value,
            })});
        console.log("Success");
    }

    render() {
        return(
            <div className="admin-container">
                <header>
                    <h1>Register</h1>
                </header>
                <button>Create Account</button>
                <button>Get Account</button>
                <button>Update Account</button>
                <button onClick={this.register.bind(this)}>Delete Account</button>
                <div className="AdminInputPanel">
                    <RegisterContent/>
                </div>
            </div>
        )
    }
}
