import React from 'react'
import './register-content.css';

export class GetContent extends React.Component {
    
    constructor() {
        super();
        this.setID = this.setID.bind(this); 
    }

    state = {
        id: 1,
        js: [],
    }   

    setID(event) {
        this.setState({id:this.ID.value});
        this.window.refresh();
    }

    componentDidMount () {
        console.log(this.state.ID);
        fetch('http://localhost:4200/api/users/' + this.state.ID, {
            method: 'get'
        }).then(res =>
            res.json().then(json => {
                console.log("J");
                this.setState({ js: json });
                //return(<div>"ID: " + json[0].ID + "\nFirst Name: " + json[0].FirstName + "\nLast Name: " + json[0].LastName + "\nEmail: " + json[0].Email + "\nPassword: " + json[0].Password</div>);
            })
        );
        console.log("J");
    }
     
    render() {
        return(
            <div className="register-container">
                <header>
                    <h1>Get Account</h1>
                </header>
                <div className='register-form'>
                    <form onSubmit={this.setID}>
                    <label>
                        ID:
                        <input ref={(ref) => {this.ID = ref}} type="number" id="input-firstname" name="id"/>
                    </label><br></br>
                    <input type="submit"/>
                    </form>
                </div>
                {/* <h1>{this.state.js[0].ID}</h1> */}
                {/* <div>"ID: " + json[0].ID + "\nFirst Name: " + json[0].FirstName + "\nLast Name: " + json[0].LastName + "\nEmail: " + json[0].Email + "\nPassword: " + json[0].Password</div> */}
                {/* <this.GetUser id={this.state.id}/> */}
            </div>
        )
    }
}
