import React from 'react';
// import blank from './Images/blank.png';
// import blanktall from './Images/blank-tall.png';
import './photoshop-content.css';

export class PhotoshopContent extends React.Component {

    constructor() {
        super();
        this.state = {
            js: undefined,
            //divtest: undefined
        }
    }

    getPhotos () {
        fetch('http://localhost:4200/api/photoshops/', {
            method: 'get'
        }).then(res =>
            res.json().then(json => {
                this.setState({ js: json});
                // var jsonFinal = "";
                // for(var i = 0; i < json.length; i++) {
                //     jsonFinal += "<img className='img' src='" + json[i].ImageUrl + "' title='" + json[i].Title + "'/>";
