import React from 'react'
import './register-content.css';

export class GetContent extends React.Component {
    
    constructor() {
        super();
        this.getUser = this.getUser.bind(this);
    }

    getUser(event) {
        event.preventDefault();
        fetch('http://localhost:4200/api/users/' + this.ID.value , {
            method: 'get'
        }).then(res =>
            res.json().then(json => {
                alert(json[0]);
            })
        );
    }
     
    render() {
        return(
            <div className="register-container">
                <header>
                    <h1>Get Account</h1>
                </header>
                <div className='register-form'>
                    <form onSubmit={this.getUser}>
                    <label>
                        ID:
                        <input ref={(ref) => {this.ID = ref}} type="number" id="input-firstname" name="id"/>
                    </label><br></br>
                    <input type="submit"/>
                    </form>
                </div>
            </div>
        )
    }
}
